(function()
{
  "use strict";

  var app = angular.module("app", ["ngRoute"]);

  app.config(function($routeProvider)
    {
      $routeProvider.when("/home",
                    {
                      templateUrl: "pages/home.html",
                      controller: "IndexPageController"
                    })
                    .when("/about",
                    {
                        templateUrl: "pages/about.html"
                    })
                    .when("/products",
                    {
                      templateUrl: "pages/products.html"
                    })
                    .when("/contact",
                    {
                      templateUrl: "pages/contact.html",
                        controller: "FormController"
                    })
                      .when("/thankyou",
                      {
                          templateUrl: "pages/thankyou.html",
                      })
                      .when("/error",
                      {
                          templateUrl: "pages/error.html",
                      })
                    .otherwise(
                    {
                      redirectTo: "/home"
                    });
    });

})();

(function ()
{
    var FormController = function($scope, $http)
    {
        $scope.submitForm = function()
        {
            if ($scope.contactForm.$valid)
            {
                emailjs.send("gmail","default",{name: $scope.name, email_from: $scope.email, content: $scope.question})
                    .then(function(response) {
                        console.log("SUCCESS. status=%d, text=%s", response.status, response.text);
                        window.location = "#thankyou";
                    }, function(err) {
                        console.log("FAILED. error=", err);
                        window.location = "#error";
                    });
            }

        };
    };

    angular.module("app").controller("FormController", ["$scope", "$http", FormController]);

})();
(function()
{
  "use strict";

  var IndexPageController = function($scope, $location)
  {
    $scope.$on('$locationChangeStart', function(event, next, current)
    {
      var title = $location.path().substr(1);
      $scope.title = title.capitalizeFirstLetter();

      $scope.showHomeHeader = false;
      $scope.classHeader = "header-other";

      if($location.path() === "/home")
      {
        $scope.showHomeHeader = true;
        $scope.classHeader = "header-home";
      }
    });
  };

  String.prototype.capitalizeFirstLetter = function()
  {
    return this.charAt(0).toUpperCase() + this.slice(1);
  };

  angular.module("app").controller("IndexPageController", ["$scope", "$location", IndexPageController]);

})();

